export default (state = 0, action) => {
    switch (action.type) {
        case 'PAYMENT':

            console.log(state,action.payload);

            const arr = [...state];
            let pay = 0;

            for (let i = 0; i < arr.length; i++) {
                pay += arr[i].price * arr[i].quantity;
            }

            return pay;
        default:
            return state;
    }
};
