import React, { useEffect, useState } from 'react';
import '../style/Faq.scss';
import { connect } from 'react-redux';
import { fetchFaq, fetchCurrentFaq } from '../action';
import CommonCotainer from './CommonContainer';


const Faq = (props) => {



    const [active, setActive] = useState("Our Food");



    const [num, setFaq] = useState(0);
    const [show, setShow] = useState({});
    const [activeIndex, setActiveIndex] = useState(false);

    useEffect(() => {
        props.fetchFaq("food");
    }, []);

    // const fetchFaqData = () => {

    //     let val = props.faq ? props?.faq["Our food"]?.queries : null;
    //     setFaq(val);
    //     console.log(props.faq, "props.faq");
    // };

    const toggleShow = (idx) => {

        let obj = {
            ...show
        }
        setShow(obj);

    };


    function handleIndexOnClick(index) {


        setFaq(index);

        console.log(index);
        if([...activeIndex].includes(index)) {
            setActiveIndex([...activeIndex].filter(idx => {
                return idx !== index;
            }));
        } else {
            setActiveIndex([...activeIndex, index]);
        }
        console.log("activeIndex",activeIndex);
      }

    // console.log(, "faq");
    // const food = props.data["Our food"].queries;

    const FAQ = props.faq;
    // console.log(props.faq,"nmnmnmnmm");

        // setFaq(props.faq["Our food"].queries);


    return (
        <div>
            <CommonCotainer heading={"Frequently Asked"} span={"Questions"} />

            <div className="faq-container">
                <div className="faq-buttons">
                    <button className={"Our Food" === active ? 'active button' : 'button'} onClick={() => {props.fetchFaq("food"); setActive("Our Food"); }}>Our Food</button>
                    <button className={"Our Delivery" === active ? 'active button' : 'button'} onClick={() => { props.fetchFaq("Delivery"); setActive("Our Delivery") }}>Our Delivery</button>
                    <button className={"Our Company" === active ? 'active button' : 'button'} onClick={() => { props.fetchFaq("Company"); setActive("Our Company") }}>Our Company</button>
                </div>

                {/* <FaqContainer FAQ={FAQ}/> */}
                <div className="faq-items">
                    {
                        FAQ ?
                            <>{
                                FAQ.map((faq, idx) => (

                                    <div className={activeIndex ? 'faq activeDiv' : 'faq'}   key={idx}>
                                        <div className="faq-question" onClick={() => setActiveIndex(!activeIndex)}>
                                            <div className="question">
                                                {faq.question}
                                            </div>
                                            <div>
                                                x
                                            </div>
                                        </div>
                                        <hr />
                                        <div  className={activeIndex ? 'activeDiv' : 'deActive'} >
                                            {<p >Lorem ipsum dolor sit amet, consectetur adipiscing elit.
                                                Suspendisse varius enim in eros elementum tristique.
                                                Duis cursus, mi quis viverra ornare, eros dolor interdum
                                                nulla, ut commodo diam libero vitae erat. Aenean
                                                faucibus nibh et justo cursus id rutrum lorem imperdiet.
                                            </p> 
                                            }
                                        </div>
                                    </div>

                                ))
                            } </> :
                            <>{
                                <div></div>
                            }</>
                    }
                </div>
            </div>
        </div>
    );
};

const mapStateToProps = (state) => {
    return { faq: state.faq};
}

export default connect(mapStateToProps, { fetchFaq })(Faq);
