export const fetchCart = (cartItems) => dispatch => {

    console.log(cartItems, "result");

    dispatch({
        type: 'CART',
        payload: cartItems
    });
};


export const fetchPayment = (items) => dispatch => {

    console.log(items, "payment");

    const arr = [...items];
    let pay = 0;

    for (let i = 0; i < arr.length; i++) {
        pay += arr[i].price * arr[i].quantity;
    }

    dispatch({
        type: 'PAYMENT',
        payload: pay
    });
};
