import React, { useEffect, useState } from 'react';
// import { Link } from 'react-router-dom';
import Modal from 'react-modal';
import { Link } from 'react-router-dom';
import '../style/Menu.scss';
import Cart from '../components/Cart';
import { fetchCart } from '../action';
import { connect } from 'react-redux';


const customStyles = {
    content: {
        top: '50%',
        bottom: 'auto',
        transform: 'translate(170%, 0%)',
    },
};

Modal.setAppElement('#modal');

const Menu = (props) => {

    const [renderMenu, setRenderMenu] = useState(props.menu);

    //    const [show, setShow] = useState(false);

    //     const [modalIsOpen, setIsOpen] = useState(false);
    //     const [quantity, setQuantity] = useState(1);
    const [updated, setUpdated] = useState(false);
    // let subtitle;


    useEffect(() => {
        setRenderMenu(props.menu);
    }, [props.menu]);


    // function openModal() {
    //     setIsOpen(true);
    // }

    // function afterOpenModal() {
    //     subtitle.style.color = '#f00';
    // }

    // function closeModal() {
    //     setIsOpen(false);
    // }

    // function addItemToCart(e) {
    //     openModal();
    //     console.log(e.target.value);
    // }

    // function clickIngredient(value) {
    //     props.fetchCart(value);
    //     openModal();
    //     let val = value;
    //     console.log(val);
    // }



    const update = (mName, e) => {
        const { name, value } = e.target;

        const arr = [...renderMenu];
        for (let i = 0; i < arr.length; i++) {
            if (arr[i].name === mName) {
                arr[i].quantity = value;
            }
        }

        setRenderMenu(arr);
        setUpdated(true);


    };


    console.log(props.menu, "myprop");
    console.log(renderMenu, "render");

    return (
        <div className="menu">
            <div className="menus">
                {
                    renderMenu ?
                        <>{
                            renderMenu.map((menu, index) => (
                                <div className="item" key={index}>
                                    <div className="menu-item">
                                        <img src={menu.image}></img>
                                        <div className="menu-content">
                                            <div className="nameAndPrice">
                                                <h6>{menu.name}</h6>
                                                <div>$ {menu.price} USD</div>
                                            </div>
                                            <p>{menu.description}</p>
                                            <div className="add">
                                                <input type={'number'} name={`quantityInput${index}`} min={1} value={menu.quantity} onChange={(e) => update(menu.name, e)} ></input>
                                                {/* <button onClick={clickIngredient.bind(this, { name: menu.name, price: menu.price, image: menu.image, quantity : quantity})}>Add to Cart</button> */}
                                                {/* <button onClick={() => setShow(true)}>Add to cart</button> */}
                                                <Cart item={menu} isUpdate={updated} />

                                            </div>
                                        </div>
                                    </div>
                                </div>
                            ))
                        }
                        </> :
                        <>
                            {
                                <div></div>
                            }
                        </>
                }

            </div>

        </div>
    );
};

const mapStateToProps = (state) => {
    return { cart: state.cart }
};

export default connect(mapStateToProps, { fetchCart })(Menu);
