export default (state = [], action) => {
    switch(action.type) {
        case 'CART' :

            const arr = state;

            for(let i=0;i<arr.length;i++) {
                if(state[i].name === action.payload.name) {
                    state[i].quantity = state[i].quantity+1;
                }
            }
            return [...arr, action.payload];
        default:
            return state;    
    }
};
