export default (state = [], action) => {
    switch(action.type) {
        case 'CART' :

            const arr = state;
            let isAdded = true;


            for(let i=0;i<arr.length;i++) {
                if(state[i].name === action.payload.name) {
                    state[i].quantity = state[i].quantity+1;
                    isAdded = false;
                }
            }

            if(isAdded) {
                return [...arr,action.payload];
            } else {
                return [...arr];
            }
            // return [...arr, action.payload];
        default:
            return state;    
    }
};
