import React, { useEffect } from 'react';
import { fetchCurrentFaq } from '../action';
import { connect } from 'react-redux';
import { useState } from 'react';

const FaqContainer = (props) => {

    const [show, setShow] = useState(false);

    useEffect(() => {
        props.fetchCurrentFaq(props.faq["Our food"].queries);
    },[]);

    return(
        <div className="faq-items">

                    {
                        props.currentFaq ?
                            <>{
                                props.currentFaq.map((faq, idx) => (
                                    <div className="faq" onClick={() => setShow(!show)} key={idx}>
                                        <div className="faq-question">
                                            <div className="question">
                                                {faq.question}
                                            </div>
                                            <div>
                                                x
                                            </div>
                                        </div>
                                        <hr />
                                        <div className="faq-answer">
                                            {show ? <p>Lorem ipsum dolor sit amet, consectetur adipiscing elit.
                                                Suspendisse varius enim in eros elementum tristique.
                                                Duis cursus, mi quis viverra ornare, eros dolor interdum
                                                nulla, ut commodo diam libero vitae erat. Aenean
                                                faucibus nibh et justo cursus id rutrum lorem imperdiet.
                                            </p> : null
                                            }

                                        </div>
                                    </div>
                                ))
                            } </> :
                            <>{
                                <div><h1></h1></div>
                            }</>
                    }
                </div>
    );
};

const mapStateToProps = (state) => {
    return { currentFaq : state.currentFaq,
            faq : state.faq }
};

export default connect(mapStateToProps, { fetchCurrentFaq })(FaqContainer);
