import React from 'react';
import '../style/Home.scss';
import Hero from '../images/Hero-Image.png';
import Trustpilot from '../images/trustpilot-logo.svg';
import Group from '../images/Group.png';
import HowItWorks from './HowItWorks';

import house from '../images/house.png';
import step1 from '../images/Step-01.png';
import step2 from '../images/Step-02.png';


const Home = () => {

    const tags = [
        {
            image : house,
            heading : 'Adapt your menu items',
            info : 'Easily adapt your menu using the webflow CMS and allow customers to browse your items.'
        },

        {
            image : step1,
            heading : 'Accept online orders & takeout',
            info : 'Let your customers order and pay via the powerful ecommerce system, or simple let them call your store.'
        },

        {
            image : step2,
            heading : 'Manage delivery or takeout',
            info : 'Manage your own logistics and take orders simply through the ecommerce system.'
        }
    ]            

    return (
        <div className="container">
            <div className="primary-container">
                <div className="flex-part-1">
                    <div className="heading">
                        <h1>Beautiful food & takeaway, <span>delivered</span> to your door</h1>
                    </div>
                    <p>Lorem Ipsum is simply dummy text of the printing and typesetting industry. Lorem Ipsum has been the industry's standard dummy text ever since the 1500.</p>
                    <div className="placeOrder">
                        <a >Place an Order</a>
                    </div>
                    <br />
                    <img src={Trustpilot}></img>
                    <h3><span>4.8 out of 5 </span>based on 2000+ reviews.</h3>
                </div>
                <div className="flex-part-2">
                    <img src={Hero}></img>
                </div>
            </div>


            <div className="primary-container bg-shape-content">
                <div className="flex-part-1 yumyum">
                    <div className="heading">
                        <h1><span>The home of fresh products</span></h1>
                    </div>
                    <p>Lorem Ipsum is simply dummy text of the printing and typesetting industry. Lorem Ipsum has been the industry's standard dummy text ever since the 1500.</p>
                    <div className="placeOrder">
                        <a >Learn about us</a>
                    </div>
                </div>
                <div className="flex-part-2">
                    <img className="img-size" src={Group}></img>
                </div>
            </div>

            <div className="HowItWorks">
                {
                    tags.map((tag,index) => {
                        <HowItWorks image={tag.image} heading ={tag.heading} info={tag.info}/>
                    })
                }
            </div>




        </div>
    );
};

export default Home;
