import React, { useEffect, useState } from 'react';
// import { Link } from 'react-router-dom';
import Modal from 'react-modal';
import { Link } from 'react-router-dom';
import '../style/Menu.scss';
// import Cart from '../components/Cart';
import { fetchCart } from '../action';
import { connect } from 'react-redux';


const customStyles = {
    content: {
        top: '50%',
        bottom: 'auto',
        transform: 'translate(170%, 0%)',
    },
};

Modal.setAppElement('#modal');

const Menu = (props) => {

   const [renderMenu, setRenderMenu] = useState(props.menu);

   const [quan, setQuan] = useState(0);


    let subtitle;
    const [modalIsOpen, setIsOpen] = useState(false);
    const [quantity, setQuantity] = useState(1);
    const [activeIndex, setActiveIndex] = useState(1);

    function openModal() {
        setIsOpen(true);
    }

    function afterOpenModal() {
        // references are now sync'd and can be accessed.
        subtitle.style.color = '#f00';
    }

    function closeModal() {
        setIsOpen(false);
    }

    function addItemToCart(e) {
        openModal();
        // props.cart({Itemname : name});
        console.log(e.target.value);
    }

    function clickIngredient(value) {
        props.fetchCart(value);
        openModal();
        let val = value;
        console.log(val);
    }



    const update = (mName,e) => {
        const {name, value} = e.target;

        const arr = [...renderMenu];

        for(let i=0;i<arr.length;i++) {
            if(arr[i].name === mName) {
                arr[i].quantity = value;
            }
        }

        setRenderMenu(arr);


        // if(idx in name) {
        //     setQuan(value);
        // }



        // console.log(idx);
        // setActiveIndex(idx);

        // setQuantity(e.target.value);
    };


    console.log(props.menu, "myprop");

    return (
        <div className="menu">
            <div className="menus">
                {
                    renderMenu ?
                        <>{
                            renderMenu.map((menu,index) => (
                                <div className="item">
                                    <div className="menu-item">
                                        <img src={menu.image}></img>
                                        <div className="menu-content">
                                            <div className="nameAndPrice">
                                                <h6>{menu.name}</h6>
                                                <div>$ {menu.price} USD</div>
                                            </div>
                                            <p>{menu.description}</p>
                                            <div className="add">
                                                <input type={'number'} name={`quantityInput${index}`} value={quan ? quan : menu.quantity} onChange={(e) => update(menu.name,e)} ></input>
                                                <button onClick={clickIngredient.bind(this, { name: menu.name, price: menu.price, image: menu.image, quantity : quantity})}>Add to Cart</button>
                                                {/* <Link to="/cart">Add to cart</Link> */}
                                                {/* <Cart/> */}

                                            </div>
                                        </div>
                                    </div>
                                </div>
                            ))
                        }
                        </> :
                        <>
                            {
                                <div></div>
                            }
                        </>
                }


                <Modal className="modal"
                    isOpen={modalIsOpen}
                    // onAfterOpen={afterOpenModal}
                    onRequestClose={closeModal}
                    style={customStyles}
                    backdropOpacity={0.3}
                    contentLabel="Example Modal"
                >
                    <div className="modal-config">
                        <div className="modal-header">
                            <h1>Your Order</h1>
                            <button onClick={closeModal}>X</button>
                        </div>
                        <hr />
                        <div className="modal-content">
                            {
                                props.cart.map((item) => (
                                    <div className="item">
                                        <img src={item.image} alt="Item Image"></img>
                                        <div className="item-desc">
                                            <div>
                                                <div className="name">{item.name}</div>
                                                <div className="price">$ {item.price} USD</div>
                                                <div className="remove">Remove</div>
                                            </div>
                                            <input></input>
                                        </div>
                                    </div>
                                ))
                            }
                        </div>
                        <div className="modal-footer">
                            <hr />
                            <div className="amount">
                                <h3>SubTotal</h3>
                                <div>Total</div>
                            </div>
                            <div className="checkout">Chechout</div>

                        </div>
                    </div>
                </Modal>

            </div>

        </div>
    );
};

const mapStateToProps = (state) => {
    return { cart: state.cart }
};

export default connect(mapStateToProps, { fetchCart })(Menu);
