import React, { useEffect, useState } from 'react';
// import { Link } from 'react-router-dom';
import Modal from 'react-modal';
import { Link } from 'react-router-dom';
import '../style/Menu.scss';
// import Cart from '../components/Cart';
import { cart } from '../action';
import { connect } from 'react-redux';


const customStyles = {
    content: {
        top: '50%',
        bottom: 'auto',
        transform: 'translate(170%, 0%)',
    },
};

Modal.setAppElement('#modal');

const Menu = (props) => {


    let subtitle;
    const [modalIsOpen, setIsOpen] = React.useState(false);

    function openModal() {
        setIsOpen(true);
    }

    function afterOpenModal() {
        // references are now sync'd and can be accessed.
        subtitle.style.color = '#f00';
    }

    function closeModal() {
        setIsOpen(false);
    }

    function addItemToCart(e) {
        // props.cart({Itemname : name});
        console.log(e.target.value);
    }

    return (
        <div className="menu">
            <div className="menus">
                {
                    props.menu ?
                        <>{
                            props.menu.map((menu) => (
                                <div className="item">
                                    <div className="menu-item">
                                        <img src={menu.image}></img>
                                        <div className="menu-content">
                                            <div className="nameAndPrice">
                                                <h6>{menu.name}</h6>
                                                <div>$ {menu.price} USD</div>
                                            </div>
                                            <p>{menu.description}</p>
                                            <div className="add">
                                                <input type="number" value="1"></input>
                                                <button onClick={openModal} addItem = {addItemToCart}>Add to Cart</button>
                                                {/* <Link to="/cart">Add to cart</Link> */}
                                                {/* <Cart/> */}

                                            </div>
                                        </div>
                                    </div>
                                </div>
                            ))
                        }
                        </> :
                        <>
                            {
                                <div></div>
                            }
                        </>
                }






                    <Modal className="modal"
                        isOpen={modalIsOpen}
                        onAfterOpen={afterOpenModal}
                        onRequestClose={closeModal}
                        style={customStyles}
                        // backdropOpacity={0.3}
                        contentLabel="Example Modal"
                    >
                        <div className="modal">
                            <div>
                                <h1>Your Order</h1>
                                <button onClick={closeModal}>X</button>
                            </div>
                            <div>

                            </div>
                        </div>
                    </Modal>


            </div>

        </div>
    );
};

const mapStateToProps = (state) => {
    return { cart : state.cart }
};

export default connect(mapStateToProps, { cart })(Menu);
