export default (state = [], action) => {
    switch (action.type) {
        case 'PAYMENT':

            const arr = [{data : [...state,action.payload]}, {payment : 0}];
            let pay = 0;
            const menu = arr[0].data;
            console.log(arr);
            for(let i=0;i<menu.length;i++) {

                pay += Number(menu[i].price) * Number(menu[i].quantity);
            }

            console.log("lalalalmamamamarararra",pay);

            // arr[1].payment = pay;

            return [{data: [...state,action.payload]}, {payment: pay}];
        default:
            return state;
    }
};
