import api from "../api";

export const fetchFaq = (tag) => async dispatch => {
    const response = await api.get(`/mini-project/faq.json`);

    console.log(response.data["Our food"].queries, "action");

    dispatch({
        type: 'FETCH_FAQ',
        payload: response.data[`Our ${tag}`].queries
    });
};

export const fetchMenu = (category) => async dispatch => {
    const response = await api.get('/mini-project/menu.json');

    console.log(response, "llllllllll");

        dispatch({
            type: 'FETCH_MENU',
            payload: response.data["menu-items"].filter((item) => {
                return item.category.includes(category);
            })
        });

};


export const fetchFullMenu = () => async dispatch => {
    const response = await api.get('/mini-project/menu.json');

    console.log(response.data["menu-items"], "fm");

        dispatch({
            type: 'FETCH_FULL_MENU',
            payload: response.data["menu-items"]
        });

};


