import React from 'react';
import { Link } from 'react-router-dom';

const Menu = (props) => {

    return (
        <div className="menu">
            <div className="menus">
                {
                    props.menu ?
                        <>{
                            props.menu.map((menu) => (
                                <div className="item">
                                    <div className="menu-item">
                                        <img src={menu.image}></img>
                                        <div className="menu-content">
                                            <div className="nameAndPrice">
                                                <h6>{menu.name}</h6>
                                                <div>$ {menu.price} USD</div>
                                            </div>
                                            <p>{menu.description}</p>
                                            <div className="add">
                                                <input type="number" value="1"></input>                                            
                                                <Link className="link" to="/cart">Add to cart</Link>


                                            </div>
                                        </div>
                                    </div>
                                </div>
                            ))
                        }
                        </> :
                        <>
                            {
                                <div></div>
                            }
                        </>
                }
            </div>
        </div>
    );
};

export default Menu;
