export default (state = [], action) => {
    switch(action.type) {
        case 'CART' :

            const arr = [...state];
            let isAdded = true;
            let pay = 0;


            for(let i=0;i<arr.length;i++) {
                if(arr[i].name === action.payload.name) {
                    console.log(arr[i].quantity,action.payload.quantity);
                    arr[i].quantity = Number(arr[i].quantity)+1;
                    isAdded = false;
                    pay += arr[i].price * arr[i].quantity;

                }
            }

            if(isAdded) {
                return [...arr,action.payload,{payment:pay}];
            } else {
                return [...arr,{payment:pay}];
            }
            // return [...state, action.payload];
        default:
            return state;    
    }
};
