import React, { useState } from 'react';
import '../style/NavBar.scss';
import logo from '../images/logo.svg';
import { Link } from 'react-router-dom';

const NavBar = () => {

    const [active, setActive] = useState(false);

    const links = [
        {
            link: "Home",
            path: "/"
        },
        {
            link: "Order",
            path: "/order"
        },
        {
            link: "Company",
            path: "/company"
        },
        {
            link: "FAQ",
            path: "/faq"
        },
        {
            link: "Contact",
            path: "/contact"
        }
    ];


    const toggleLink = (e) => {
        if(active) {
            e.currentTarget.classList.toggle('active');
        }

    }

    return (
        <div className="container">
            <div className="bannar">
                <div className="banner-wrap">
                    <div className="info">
                        We're open and available for takeaway & delivery.
                        <a className="order-link"> Order Now</a>
                    </div>
                </div>
            </div>

            <div className="nav-container">
                <div className="container-div">
                    <div className="logo">
                        <Link to='/'>
                            <img src={logo} alt='image'></img>
                        </Link>
                    </div>
                    <div className="nav-links">

                        {
                            links.map((link) => (
                                <Link
                                    className='nav-link' to={link.path}
                                    // onStateChange={() => setActive(!active)}
                                    onClick={toggleLink}
                                >
                                    {link.link}
                                </Link>
                            ))
                        }

                        {/* <Link className="nav-link" to='/'>Home</Link>
                        <Link className="nav-link" to='/order'>Order</Link>
                        <Link className="nav-link" to='/company'>Company</Link>
                        <Link className="nav-link" to='/faq'>FAQ</Link>
                        <Link className="nav-link" to="/contact">Contact</Link> */}

                        <div className="cart">
                            <a href="#" className="cart-button">
                                <svg width="17px" height="17px" viewBox="0 0 17 17">
                                    <g stroke="none" stroke-width="1" fill="none" fill-rule="evenodd">
                                        <path d="M2.60592789,2 L0,2 L0,0 L4.39407211,0 L4.84288393,4 L16,4 L16,9.93844589 L3.76940945,12.3694378 L2.60592789,2 Z M15.5,17 C14.6715729,17 14,16.3284271 14,15.5 C14,14.6715729 14.6715729,14 15.5,14 C16.3284271,14 17,14.6715729 17,15.5 C17,16.3284271 16.3284271,17 15.5,17 Z M5.5,17 C4.67157288,17 4,16.3284271 4,15.5 C4,14.6715729 4.67157288,14 5.5,14 C6.32842712,14 7,14.6715729 7,15.5 C7,16.3284271 6.32842712,17 5.5,17 Z" fill="white" fill-rule="nonzero" />
                                    </g>
                                </svg>
                            </a>
                        </div>

                    </div>
                </div>
            </div>
        </div>
    );
};

export default NavBar;
