import React, { useEffect, useState } from 'react';
import '../style/Faq.scss';
import { connect } from 'react-redux';
import { fetchFaq } from '../action';
import CommonCotainer from './CommonContainer';

const Faq = (props) => {

    const [faq, setFaq] = useState([]);
    const [show, setShow] = useState(false);

    useEffect(() => {
        props.fetchFaq();
        fetchFaqData();
    }, [props.faq]);


    const fetchFaqData = () => {

        let val = props.faq ? props?.faq["Our food"]?.queries : null;
        setFaq(val);

    };


    // console.log(, "faq");

    return (
        <div>
            <CommonCotainer heading={"Frequently Asked"} span={"Questions"} />

            <div className="faq-container">
                <div className="faq-buttons">
                    <button className="button" onClick={() => { setFaq(props.faq["Our food"].queries); console.log("Food") }}>Our Food</button>
                    <button className="button" onClick={() => { setFaq(props.faq["Our Delivery"].queries); console.log("Delivery") }}>Our Delivery</button>
                    <button className="button" onClick={() => { setFaq(props.faq["Our Company"].queries); console.log("Company") }}>Our Company</button>
                </div>
                <div className="faq-items">

                    {
                        faq ?
                            <>{
                                faq.map((faq,idx) => (
                                    <div className="faq" onClick={()=>setShow(!show)} key={idx}>
                                        <div className="faq-question">
                                            <div className="question">
                                                {faq.question}
                                            </div>
                                            <div>
                                                x
                                            </div>
                                        </div>
                                        <hr />
                                        <div className="faq-answer">
                                            { show ? <p>Lorem ipsum dolor sit amet, consectetur adipiscing elit.
                                                Suspendisse varius enim in eros elementum tristique.
                                                Duis cursus, mi quis viverra ornare, eros dolor interdum
                                                nulla, ut commodo diam libero vitae erat. Aenean
                                                faucibus nibh et justo cursus id rutrum lorem imperdiet.
                                            </p> : null
                                            }

                                        </div>
                                    </div>
                                ))
                            } </> :
                            <>{
                                <div><h1></h1></div>
                            }</>
                    }
                </div>
            </div>
        </div>
    );
};

const mapStateToProps = (state) => {
    return { faq: state.faq };
}

export default connect(mapStateToProps, { fetchFaq })(Faq);
