export default (state = [], action) => {
    switch (action.type) {
        case 'PAYMENT':

            const arr = [{data : [...state,action.payload]}];

            let pay = 0;
            const menu = arr[0].data;

            for(let i=0;i<menu.length;i++) {
                pay += menu[i].price * menu[i].quantity;
            }

            arr.push({payment: pay});

            console.log(arr);



            return arr;
        default:
            return state;
    }
};
