    useEffect(() => {
        props.fetchFullMenu();
    },[]);

    const MENU = props.fullMenu;
    console.log(MENU,"ppppppppppppppppppp");

    return (
        <div>
            <CommonCotainer heading={"Get your food"} span={"delivered"} heading2={"or"} span2={"pick-up"} heading3={"in store."} />
            <div className="menu-container">
                <div className="title-wrap">
                    <div className="title">
                        <h1>Browse Our Menu</h1>
                        <p>Use our menu to place an order online, or phone our store to
