                <div className="menu-items">
                    <div className="buttons">
                        <button className="button" onClick={() => { props.fetchMenu("") }}>Burgers</button>
                        <button className="button" onClick={() => { props.fetchMenu("Burgers") }}>Burgers</button>
                        <button className="button" onClick={() => { props.fetchMenu("Sides") }}>sides</button>
                        <button className="button" onClick={() => { props.fetchMenu("Drinks") }}>Drinks</button>
                    </div>
                    <Menu menu={MENU} />
