import React, { Fragment, useEffect, useState } from "react";
import { connect } from "react-redux";
import Modal from 'react-modal';
import { fetchCart } from "../action";
import '../style/Menu.scss';

const customStyles = {
    content: {
        position:'fixed',
        right:"0%",
        top: '0',
        bottom:'auto'
    },
};

Modal.setAppElement('#modal');

const Cart = (props) => {

    let subtitle;
    const [modalIsOpen, setIsOpen] = React.useState(false);
    const [cart, setCart] = useState([]);

    useEffect(() => {
       setCart(props.cart);
    },[props.cart]);

    function openModal() {
        setIsOpen(true);
    }

    function afterOpenModal() {
        // references are now sync'd and can be accessed.
        subtitle.style.color = '#f00';
    }

    function closeModal() {
        setIsOpen(false);

    }

    const update = (mName,e) => {
        const {name, value} = e.target;

        const arr = [...cart];
        for(let i=0;i<arr.length;i++) {
            if(arr[i].name === mName) {
                arr[i].quantity = value;
            }
        }
        setCart(arr);
    };

    return (
        <Fragment>
            <button

            onClick={() =>{openModal(); props.fetchCart(props.item); console.log(props.item);}}
            >
                Add to cart
            </button>
            <Modal className="modal"
                isOpen={modalIsOpen}
                // onAfterOpen={afterOpenModal}
                onRequestClose={closeModal}
                style={customStyles}
                backdropOpacity={0.3}
                contentLabel="Example Modal"
            >
                <div className="modal-config">
                    <div className="modal-header">
                        <h1>Your Order</h1>
                        <button onClick={closeModal}>X</button>
                    </div>
                    <hr />
                    <div className="modal-content">
                        {
                            cart.map((item,index) => (
                                <div className="item" key={index}>
                                    <img src={item.image} alt="Item Image"></img>
                                    <div className="item-desc">
                                        <div>
                                            <div className="name">{item.name}</div>
                                            <div className="price">$ {item.price} USD</div>
                                            <div className="remove">Remove</div>
                                        </div>
                                        <input value={item.quantity} onChange={(e) => update(item.name,e)}></input>
                                    </div>
                                </div>
                            ))
                        }
                    </div>
                    <div className="modal-footer">
                        <hr />
                        <div className="amount">
                            <h3>SubTotal</h3>
                            <div>{props.cart["payment"]}</div>
                        </div>
                        <div className="checkout">Chechout</div>

                    </div>
                </div>
            </Modal>
        </Fragment>
    );

}

const mapStateToProps = (state) => {
    return { cart: state.cart }
};

export default connect(mapStateToProps, { fetchCart })(Cart);
