export default (state = 0, action) => {
    switch (action.type) {
        case 'PAYMENT':


            let pay = 0;

            const arr = [...state];
            console.log(arr);



            for (let i = 0; i < arr.length; i++) {
                pay += arr[i].price * arr[i].quantity;
            }

            return pay;
        default:
            return 0;
    }
};
