import React, { useEffect, useState } from 'react';
// import { Link } from 'react-router-dom';
import Modal from 'react-modal';
import { Link } from 'react-router-dom';
import '../style/Menu.scss';
// import Cart from '../components/Cart';
import { fetchCart } from '../action';
import { connect } from 'react-redux';


const customStyles = {
    content: {
        top: '50%',
        bottom: 'auto',
        transform: 'translate(170%, 0%)',
    },
};

Modal.setAppElement('#modal');

const Menu = (props) => {


    let subtitle;
    const [modalIsOpen, setIsOpen] = useState(false);
    const [quantity, setQuantity] = useState(1);

    function openModal() {
        setIsOpen(true);
    }

    function afterOpenModal() {
        // references are now sync'd and can be accessed.
        subtitle.style.color = '#f00';
    }

    function closeModal() {
        setIsOpen(false);
    }

    function addItemToCart(e) {
        openModal();
        // props.cart({Itemname : name});
        console.log(e.target.value);
    }

    function clickIngredient(value) {
        props.fetchCart(value);
        openModal();
        let val = value;
        console.log(val);
    }

    function updateQuantity(quan, index) {
        props.menu[index].quantity = quan;
        // setQuantity(quan); 
      }


    console.log(props.cart, "myprop");

    return (
        <div className="menu">
            <div className="menus">
                {
                    props.menu ?
                        <>{
                            props.menu.map((menu,index) => (
                                <div className="item">
                                    <div className="menu-item">
                                        <img src={menu.image}></img>
                                        <div className="menu-content">
                                            <div className="nameAndPrice">
                                                <h6>{menu.name}</h6>
                                                <div>$ {menu.price} USD</div>
                                            </div>
                                            <p>{menu.description}</p>
                                            <div className="add">
                                                <input type="number" value={quantity} onChange={(e) => this.updateQuantity(e.target.value, index)} ></input>
                                                <button onClick={clickIngredient.bind(this, { name: menu.name, price: menu.price, image: menu.image })}>Add to Cart</button>
                                                {/* <Link to="/cart">Add to cart</Link> */}
                                                {/* <Cart/> */}

                                            </div>
                                        </div>
                                    </div>
                                </div>
                            ))
                        }
                        </> :
                        <>
                            {
                                <div></div>
                            }
                        </>
                }


                <Modal className="modal"
                    isOpen={modalIsOpen}
                    // onAfterOpen={afterOpenModal}
                    onRequestClose={closeModal}
                    style={customStyles}
                    backdropOpacity={0.3}
                    contentLabel="Example Modal"
                >
                    <div className="modal-config">
                        <div className="modal-header">
                            <h1>Your Order</h1>
                            <button onClick={closeModal}>X</button>
                        </div>
                        <hr />
                        <div className="modal-content">
                            {
                                props.cart.map((item) => (
                                    <div className="item">
                                        <img src={item.image} alt="Item Image"></img>
                                        <div className="item-desc">
                                            <div>
                                                <div className="name">{item.name}</div>
                                                <div className="price">$ {item.price} USD</div>
                                                <div className="remove">Remove</div>
                                            </div>
                                            <input></input>
                                        </div>
                                    </div>
                                ))
                            }
                        </div>
                        <div className="modal-footer">
                            <hr />
                            <div className="amount">
                                <h3>SubTotal</h3>
                                <div>Total</div>
                            </div>
                            <div className="checkout">Chechout</div>

                        </div>
                    </div>
                </Modal>


            </div>

        </div>
    );
};

const mapStateToProps = (state) => {
    return { cart: state.cart }
};

export default connect(mapStateToProps, { fetchCart })(Menu);
